import React, { useRef, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate hook
import PhoneIcon from "@mui/icons-material/Phone";
import HomeIcon from "@mui/icons-material/Home";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import AccountBalanceIcon from "@mui/icons-material/AccountBalance";
import VerifiedUserIcon from "@mui/icons-material/VerifiedUser";
import "./header.css";
import logo from "../../assets/images/logo.png";

export const Header = () => {
  const ctaButtonRef = useRef(null);
  const iconsRef = useRef(null);
  const navigate = useNavigate(); // Initialize useNavigate hook

  // Function to handle navigation to the contact page
  const handleContactClick = () => {
    navigate('/contact'); // Redirect to the contact page
  };

  const handleMouseLeave = (event) => {
    // If you want to handle mouse leave functionality, keep this part
    if (
      ctaButtonRef.current &&
      iconsRef.current &&
      !ctaButtonRef.current.contains(event.relatedTarget) &&
      !iconsRef.current.contains(event.relatedTarget)
    ) {
      // setShowIcons(false); // Comment out this if not using icon toggle
    }
  };

  useEffect(() => {
    const ctaButtonElement = ctaButtonRef.current;
    const iconsElement = iconsRef.current;

    if (ctaButtonElement && iconsElement) {
      ctaButtonElement.addEventListener("mouseleave", handleMouseLeave);
      iconsElement.addEventListener("mouseleave", handleMouseLeave);
    }

    return () => {
      if (ctaButtonElement) {
        ctaButtonElement.removeEventListener("mouseleave", handleMouseLeave);
      }
      if (iconsElement) {
        iconsElement.removeEventListener("mouseleave", handleMouseLeave);
      }
    };
  }, []);

  return (
    <header className="header">
      <div className="logo">
        <img src={logo} alt="logo" className="logo-image" />
      </div>
      <nav>
        <ul>
          <li>
            <Link to="/"><HomeIcon className="nav-icon" /> Home</Link>
          </li>
          <li>
            <Link to="/accounting-services"><AccountBalanceIcon className="nav-icon" /> Accounting Services</Link>
          </li>
          <li>
            <Link to="/dsc"><VerifiedUserIcon className="nav-icon" /> DSC Services</Link>
          </li>
          <li>
            <Link to="/about"><AccountCircleIcon className="nav-icon" /> About Us</Link>
          </li>
        </ul>
      </nav>
      <div className="cta-section">
        <div className="cta-button" ref={ctaButtonRef}>
          <button onClick={handleContactClick}>
            <PhoneIcon className="cta-icon" /> CONTACT US
          </button>
        </div>
        {/* <div className="user-icon">
          <Link to="/user">
            <AccountCircleIcon />
          </Link>
        </div> */}
      </div>
    </header>
  );
};
