import React, { useState } from "react";
import "../DscUp/dscup.css";

const DscUp = () => {
  const [videoFile, setVideoFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);

  // Handle file selection
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith("video/")) {
      setVideoFile(file);
      setPreviewURL(URL.createObjectURL(file)); // Generate preview URL
    } else {
      alert("Please select a valid video file.");
    }
  };

  // Handle video upload (Placeholder for actual upload logic)
  const handleUpload = () => {
    if (!videoFile) {
      alert("Please select a video first.");
      return;
    }
    alert(`Submitting ${videoFile.name} for DSC video verification.`);
    // Add your actual upload logic here
  };

  return (
    <div className="upload-container">
      <h1>DSC Video Verification</h1>
      <p>
        Please follow these steps for DSC video verification:
      </p>
      <ol className="verification-steps">
        <li>Record a clear video in a well-lit environment.</li>
        <li>
          Ensure your face and government-issued ID are clearly visible.
        </li>
        <li>
          Clearly state your full name and DSC details at the start of the video.
        </li>
        <li>
          Once recorded, use the form below to select and upload your video.
        </li>
      </ol>
      <p>Select a video file to upload for verification.</p>

      {/* File input */}
      <input 
        type="file" 
        accept="video/*" 
        onChange={handleFileChange} 
        className="file-input"
      />

      {/* Preview video */}
      {previewURL && (
        <div className="video-preview">
          <video width="300" controls>
            <source src={previewURL} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}

      {/* Upload button */}
      <button 
        onClick={handleUpload} 
        className="upload-button"
      >
        Click here to upload video
      </button>
    </div>
  );
};

export default DscUp;
