import React from "react";
import { useNavigate } from "react-router-dom";
import "./DscPage.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFileSignature, faGlobe } from "@fortawesome/free-solid-svg-icons";

// Card Component: Reusable component for displaying card content
const Card = ({ title, description, onClick, icon }) => (
  <div className="transparent-card">
    <div className="transparent-card-header">
      {icon && <span className="card-icon">{icon}</span>} {/* Display icon if provided */}
      <span>{title}</span>
    </div>
    <div className="transparent-card-content">
      {description}
    </div>
    <div className="transparent-card-footer">
      <button className="btn-primary" onClick={onClick}>More</button>
    </div>
  </div>
);

// DscPage Component: Main page for displaying DSC options
const DscPage = () => {
  const navigate = useNavigate();

  return (
    <div className="dsc-page">
      <header className="header">
        {/* Optional navigation */}
      </header>
      <main className="main">
        <section className="hero">
          <h1>Explore Our <span>DSC</span> Services</h1>
          <div className="cards">
            {/* Card for Foreign DSC with specific details */}
            <Card 
              title="Foreign DSC"
              description="Get your Digital Signature Certificate for foreign use, handled by our professionals."
              icon={<FontAwesomeIcon icon={faGlobe} />} // World icon for foreign DSC
              onClick={() => navigate("/dsc-detail-page")} // Navigate to /foreign-dsc on click
            />
            {/* Card for Indian DSC with specific details */}
            <Card 
              title="Indian DSC"
              description="Obtain your Digital Signature Certificate for use in India, guided by our experts."
              icon={<FontAwesomeIcon icon={faFileSignature} />} // File signature icon for Indian DSC
              onClick={() => navigate("/consulting-page")} 
            />
          </div>
          <button className="go-back-btn" onClick={() => navigate("/")}>Go back</button>
        </section>
      </main>
    </div>
  );
}

export default DscPage;
