// export default Home;
import React from "react";
import { useNavigate } from "react-router-dom";
import { category } from "../../assets/data/data";
import HomeIcon from '@mui/icons-material/Home';
import './home.css';
import sampleVideo from '../../assets/videos/videoacc.mp4'; 
import backgroundVideo from '../../assets/videos/money.mp4'; 

const Home = () => {
  const navigate = useNavigate();

  const handleCardClick = (category) => {
    navigate(`/${category.toLowerCase().replace(' ', '-')}`);
  };

  return (
    <div className="home-container">
      <video autoPlay loop muted className="background-video">
        <source src={backgroundVideo} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <header className="home-header">
      <div className="home-icon" onClick={() => navigate('/')}>
          <HomeIcon fontSize="large" />
        </div>
        <h1>Categories</h1>
      </header>
      <div className="content-overlay">
        <div className="category-container">
          {category.map((item) => (
            <div 
              key={item.id} 
              className="category-card" 
              onClick={() => handleCardClick(item.category)}
            >
              <div className="media-container">
                {item.category === "ACCOUNTING SERVICES" ? (
                  <video className="category-video" autoPlay loop muted>
                    <source src={sampleVideo} type="video/mp4" />
                    Your browser does not support the video tag.
                  </video>
                ) : (
                  <img src={item.cover} alt={item.title} className="category-image" />
                )}
                <div className="overlay-text">
                  <h3>{item.category}</h3>
                  <p>{item.title}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
