import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
// import axios from 'axios';
import { gql, useMutation } from '@apollo/client'; // Import Apollo Client's useMutation
import '../../services/Consulting/consulting.css';

// Define the GraphQL mutation
const ADD_CONSULTING_INQUIRY = gql`
  mutation AddConsultingInquiry($name: String!, $email: String!, $phone: String!, $message: String!) {
    addConsultingInquiry(input: { name: $name, email: $email, phone: $phone, message: $message }) {
      success
      message
    }
  }
`;

const ConsultingPage = () => {
  const navigate = useNavigate();

  const [addConsultingInquiry, { data, error }] = useMutation(ADD_CONSULTING_INQUIRY);

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      phone: '',
      message: '',
    },
    validationSchema: Yup.object({
      name: Yup.string()
        .matches(/^[a-zA-Z\s]{2,50}$/, 'Please enter a valid name (2-50 characters, letters only).')
        .required('Name is required'),
      email: Yup.string()
        .email('Invalid email address')
        .required('Email is required'),
      phone: Yup.string()
        .matches(/^\d{10}$/, 'Please enter a valid 10-digit phone number.')
        .required('Phone number is required'),
      message: Yup.string()
        .min(10, 'Message should be at least 10 characters long and provide meaningful content.')
        .required('Message is required'),
    }),
    onSubmit: async (values, { setSubmitting, resetForm }) => {
      try {
        // Call the GraphQL mutation
        const response = await addConsultingInquiry({
          variables: {
            name: values.name,
            email: values.email,
            phone: values.phone,
            message: values.message,
          },
        });

        if (response.data.addConsultingInquiry.success) {
          console.log('Inquiry submitted successfully');
          resetForm();
        } else {
          console.error('Error submitting inquiry:', response.data.addConsultingInquiry.message);
        }
      } catch (err) {
        console.error('Failed to submit inquiry', err);
      } finally {
        setSubmitting(false);
      }
    },
  });

  return (
    <div className="consulting-page">
      {/* Same UI as before */}
      <header className="consulting-header">
        <h1>Consulting Services</h1>
      </header>
      <main className="consulting-content">
        <div className="consulting-main">
          <div className="consulting-info">
            <div className="consulting-text">
              <p>
                {/* Consulting page text */}
              </p>
              <h1>Documents Needed for Consultancy Registration</h1>
              <p>
                {/* Documents list */}
              </p>
              <h3>Enroll or contact us today to schedule a consultation and take the first step towards optimizing your business.</h3>
            </div>
          </div>
          <div className="phone-frame">
            <div className="phone-screen">
              <div className="contact-form">
                <h2>Enroll Now</h2>
                <form onSubmit={formik.handleSubmit}>
                  <label aria-label="Name">
                    Name:
                    <input
                      type="text"
                      name="name"
                      value={formik.values.name}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.name && formik.errors.name ? (
                      <div className="error-message">{formik.errors.name}</div>
                    ) : null}
                  </label>
                  <label aria-label="Email">
                    Email:
                    <input
                      type="email"
                      name="email"
                      value={formik.values.email}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.email && formik.errors.email ? (
                      <div className="error-message">{formik.errors.email}</div>
                    ) : null}
                  </label>
                  <label aria-label="Phone">
                    Phone:
                    <input
                      type="tel"
                      name="phone"
                      value={formik.values.phone}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.phone && formik.errors.phone ? (
                      <div className="error-message">{formik.errors.phone}</div>
                    ) : null}
                  </label>
                  <label aria-label="Message">
                    Message:
                    <textarea
                      name="message"
                      value={formik.values.message}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.message && formik.errors.message ? (
                      <div className="error-message">{formik.errors.message}</div>
                    ) : null}
                  </label>
                  <button type="submit" className="submit-btn" disabled={formik.isSubmitting}>
                    {formik.isSubmitting ? <div className="loading-spinner"></div> : 'Submit'}
                  </button>
                </form>
                {error && <p className="error-message">{error.message}</p>}
              </div>
            </div>
          </div>
        </div>
      </main>
      <div className="advisor">
        <button onClick={() => navigate('/accounting-services')} className="back-btn">Go back</button>
      </div>
    </div>
  );
};

export default ConsultingPage;
