import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { gql, useMutation } from '@apollo/client';  
import '../../services/Consulting/consulting.css';

const SUBMIT_ENROLLMENT = gql`
  mutation SubmitEnrollment($name: String!, $email: String!, $phone: String!, $message: String!) {
    submitEnrollment(name: $name, email: $email, phone: $phone, message: $message)
  }
`;

const ConsultingPage = () => {
  const navigate = useNavigate();

  // Initialize the mutation using Apollo's useMutation hook
  const [submitEnrollment, { loading, error, data }] = useMutation(SUBMIT_ENROLLMENT);

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      phone: '',
      message: '',
    },
    validationSchema: Yup.object({
      name: Yup.string()
        .matches(/^[a-zA-Z\s]{2,50}$/, 'Please enter a valid name (2-50 characters, letters only).')
        .required('Name is required'),
      email: Yup.string()
        .email('Invalid email address')
        .required('Email is required'),
      phone: Yup.string()
        .matches(/^\d{10}$/, 'Please enter a valid 10-digit phone number.')
        .required('Phone number is required'),
      message: Yup.string()
        .min(10, 'Message should be at least 10 characters long and provide meaningful content.')
        .required('Message is required'),
    }),
    onSubmit: async (values, { setSubmitting, setErrors, resetForm }) => {
      try {
        // Call the GraphQL mutation when form is submitted
        const response = await submitEnrollment({
          variables: {
            name: values.name,
            email: values.email,
            phone: values.phone,
            message: values.message,
          },
        });

        console.log(response.data.submitEnrollment);  // Handle the success response
        resetForm();  // Reset the form after successful submission
      } catch (err) {
        setErrors({ submit: 'Failed to send the form. Please try again.' });  // Handle errors
      } finally {
        setSubmitting(false);  // End form submission state
      }
    },
  });

  return (
    <div className="consulting-page">
      <header className="consulting-header">
        <h1>Consulting Services</h1>
      </header>
      <main className="consulting-content">
        <div className="consulting-main">
          <div className="consulting-info">
            <div className="consulting-text">
              {/* Your text content here */}
              <h3>
                Enroll or contact us today to schedule a consultation and take the first step towards optimizing your business.
              </h3>
            </div>
          </div>
          <div className="phone-frame">
            <div className="phone-screen">
              <div className="contact-form">
                <h2>Enroll Now</h2>
                <form onSubmit={formik.handleSubmit}>
                  <label>
                    Name:
                    <input
                      type="text"
                      name="name"
                      value={formik.values.name}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.name && formik.errors.name ? (
                      <div className="error-message">{formik.errors.name}</div>
                    ) : null}
                  </label>
                  <label>
                    Email:
                    <input
                      type="email"
                      name="email"
                      value={formik.values.email}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.email && formik.errors.email ? (
                      <div className="error-message">{formik.errors.email}</div>
                    ) : null}
                  </label>
                  <label>
                    Phone:
                    <input
                      type="tel"
                      name="phone"
                      value={formik.values.phone}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.phone && formik.errors.phone ? (
                      <div className="error-message">{formik.errors.phone}</div>
                    ) : null}
                  </label>
                  <label>
                    Message:
                    <textarea
                      name="message"
                      value={formik.values.message}
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      required
                    />
                    {formik.touched.message && formik.errors.message ? (
                      <div className="error-message">{formik.errors.message}</div>
                    ) : null}
                  </label>
                  <button type="submit" className="submit-btn" disabled={formik.isSubmitting || loading}>
                    {loading ? <div className="loading-spinner"></div> : 'Submit'}
                  </button>
                </form>
                {formik.errors.submit && <p className="error-message">{formik.errors.submit}</p>}
                {error && <p className="error-message">Error: {error.message}</p>}
                {data && <p className="success-message">{data.submitEnrollment}</p>}
              </div>
            </div>
          </div>
        </div>
      </main>
      <div className="advisor">
        <button onClick={() => navigate('/accounting-services')} className="back-btn">Go back</button>
      </div>
    </div>
  );
};

export default ConsultingPage;
