import React from "react";
import { useNavigate } from "react-router-dom";
import "../acc/AccPage.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFileSignature } from "@fortawesome/free-solid-svg-icons"; 

// Card Component: Reusable component for displaying card content with customizable background image
const Card = ({ title, description, onClick, icon, backgroundImage }) => (
  <div className="transparent-card" style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>
    <div className="transparent-card-header">
      {icon && <span className="card-icon">{icon}</span>} {/* Display icon if provided */}
      <span>{title}</span>
    </div>
    <div className="transparent-card-content">
      {description}
    </div>
    <div className="transparent-card-footer">
      <button className="btn-primary" onClick={onClick}>More</button>
    </div>
  </div>
);

// DscPage Component: Main page for displaying DSC options
const DscPage = () => {
  const navigate = useNavigate();

  // Paths to background images for different cards
  const foreignBackgroundImage = "path/to/foreign-world-icon.png"; // Replace with the actual path to the foreign world icon image
  const indianBackgroundImage = "path/to/indian-flag-icon.png"; // Replace with the actual path to the Indian flag icon image

  return (
    <div className="dsc-page">
      <header className="header">
        {/* Optional navigation */}
      </header>
      <main className="main">
        <section className="hero">
          <h1>Explore Our <span>DSC</span></h1>
          <div className="cards">
            {/* Card for DSC Foreign Individual with a background image of a world icon */}
            <Card 
              title="DSC Foreign Individual"
              description="Professional will complete your DSC for foreign individuals."
              icon={<FontAwesomeIcon icon={faFileSignature} />} 
              onClick={() => navigate("/co")} // Navigate to /co on click
              backgroundImage={foreignBackgroundImage} // Foreign world background image
            />
            {/* Card for DSC Individual with a background image of an Indian flag */}
            <Card 
              title="DSC Individual"
              description="Professional will complete your DSC for individual use."
              icon={<FontAwesomeIcon icon={faFileSignature} />} 
              onClick={() => navigate("/consulting-page")} // Navigate to /consulting-page on click
              backgroundImage={indianBackgroundImage} // Indian flag background image
            />
          </div>
          <button className="go-back-btn" onClick={() => navigate("/")}>Go back</button> {/* Navigate to home on click */}
        </section>
      </main>
    </div>
  );
}

export default DscPage;
