import React from "react";
import { useNavigate } from "react-router-dom";
import "./DscPage.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFileSignature, faGlobe } from "@fortawesome/free-solid-svg-icons"; 
// import backgroundImage from "../../assets/images/dsc.jpg"; // Correct path to the background image

// Card Component: Reusable component for displaying card content
const Card = ({ title, description, onClick, icon }) => (
  <div className="transparent-card">
    <div className="transparent-card-header">
      {icon && <span className="card-icon">{icon}</span>} {/* Display icon if provided */}
      <span>{title}</span>
    </div>
    <div className="transparent-card-content">
      {description}
    </div>
    <div className="transparent-card-footer">
      <button className="btn-primary" onClick={onClick}>More</button>
    </div>
  </div>
);

// DscPage Component: Main page for displaying DSC options
const DscPage = () => {
  const navigate = useNavigate();

  return (
    <div 
      className="dsc-page" 
      style={{
        backgroundImage: `url(${backgroundImage})`, // Set the background image for the page
        backgroundSize: 'cover', // Make sure the image covers the entire page
        backgroundPosition: 'center', // Center the background image
        backgroundRepeat: 'no-repeat', // Prevent the background image from repeating
        minHeight: '100vh', // Ensure the background covers the full viewport height
        padding: '20px' // Add padding for content spacing
      }}
    >
      <header className="header">
        {/* Optional navigation */}
      </header>
      <main className="main">
        <section className="hero1">
          <h1>Explore Our <span>DSC</span></h1>
          <div className="cards">
            {/* Card for DSC Foreign Individual with background icon */}
            <Card 
              title="DSC Foreign Individual"
              description="Professional will complete your DSC for foreign individuals."
              icon={<FontAwesomeIcon icon={faGlobe} />} // World icon
              onClick={() => navigate("/co")} // Navigate to /co on click
            />
            {/* Card for DSC Individual */}
            <Card 
              title="DSC Individual"
              description="Professional will complete your DSC for individual use."
              icon={<FontAwesomeIcon icon={faFileSignature} />} 
              onClick={() => navigate("/consulting-page")} 
            />
          </div>
          <button className="go-back-btn" onClick={() => navigate("/")}>Go back</button>
        </section>
      </main>
    </div>
  );
}

export default DscPage;
