import React, { useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate
import PhoneIcon from "@mui/icons-material/Phone";
import HomeIcon from "@mui/icons-material/Home";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import AccountBalanceIcon from "@mui/icons-material/AccountBalance";
import VerifiedUserIcon from "@mui/icons-material/VerifiedUser";
import "./header.css";
import logo from "../../assets/images/logo.png";

export const Header = () => {
//   const [showMoneyEffect, setShowMoneyEffect] = useState(false);
  const ctaButtonRef = useRef(null);
  const navigate = useNavigate(); // Initialize useNavigate

  const handleClick = () => {
    setShowMoneyEffect(true);
    setTimeout(() => setShowMoneyEffect(false), 1000); // Reset animation after 1s
  };

  const handleContactClick = () => {
    // Navigate to the contact page
    navigate("/contact");
  };

  return (
    <header className="header">
      <div className="logo">
        <img src={logo} alt="logo" className="logo-image" />
      </div>
      <nav>
        <ul>
          <li className="nav-item">
            <Link to="/" onClick={handleClick}>
              <HomeIcon className="nav-icon" /> Home
              {showMoneyEffect && <div className="money-effect">$</div>}
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/accounting-services" onClick={handleClick}>
              <AccountBalanceIcon className="nav-icon" /> Accounting Services
              {showMoneyEffect && <div className="money-effect">$</div>}
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/dsc" onClick={handleClick}>
              <VerifiedUserIcon className="nav-icon" /> DSC Services
              {showMoneyEffect && <div className="money-effect">$</div>}
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/about" onClick={handleClick}>
              <AccountCircleIcon className="nav-icon" /> About Us
              {showMoneyEffect && <div className="money-effect">$</div>}
            </Link>
          </li>
        </ul>
      </nav>
      <div className="cta-section">
        <div className="cta-button" ref={ctaButtonRef}>
          <button onClick={handleContactClick}>
            <PhoneIcon className="cta-icon" /> CONTACT US
          </button>
        </div>
      </div>
    </header>
  );
};
