import React, { useState, useRef } from "react";
import { FaVideo, FaUpload, FaClipboardCheck } from "react-icons/fa"; // Icons for steps
import "../DscUp/dscup.css";

const VideoVerificationSteps = () => {
  const [recording, setRecording] = useState(false);
  const [videoBlob, setVideoBlob] = useState(null);
  const [videoURL, setVideoURL] = useState(null);
  const mediaRecorderRef = useRef(null);
  const streamRef = useRef(null);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      streamRef.current = stream;
      const mediaRecorder = new MediaRecorder(stream);
      let chunks = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const videoData = new Blob(chunks, { type: "video/webm" });
        const videoURL = URL.createObjectURL(videoData);
        setVideoBlob(videoData);
        setVideoURL(videoURL);
        chunks = [];
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();
      setRecording(true);

      // Stop recording automatically after 15 or 30 seconds
      setTimeout(() => {
        mediaRecorder.stop();
        setRecording(false);
      }, 15000); // Change to 30000 for 30 seconds
    } catch (error) {
      console.error("Error accessing webcam:", error);
    }
  };

  const uploadVideo = async () => {
    if (!videoBlob) {
      alert("Please record a video first!");
      return;
    }

    const formData = new FormData();
    formData.append("video", videoBlob, "verification-video.webm");

    try {
      const response = await fetch("https://your-backend-api.com/upload", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        alert("Video uploaded successfully!");
      } else {
        alert("Failed to upload video.");
      }
    } catch (error) {
      console.error("Upload error:", error);
    }
  };

  return (
    <div className="verification-container">
      <h1>How to Do DSC Video Verification</h1>

      <div className="step-box">
        <h2><FaVideo className="step-icon" /> Step 1: Prepare for Recording</h2>
        <ul>
          <li>Find a <strong>well-lit environment</strong>.</li>
          <li>Ensure your <strong>face and voice</strong> are clear.</li>
        </ul>
      </div>

      <div className="step-box">
        <h2><FaVideo className="step-icon" /> Step 2: Record Your Video</h2>
        <ol>
          <li>Click <strong>"Start Recording"</strong>.</li>
          <li>Clearly state <strong>your full name and DSC details along with a 3-digit code</strong>.</li>
          <li>Recording will <strong>automatically stop after 15 seconds</strong>.</li>
          <li>Preview your video before uploading.</li>
        </ol>
        
      </div>

      {videoURL && (
        <div className="video-preview">
          <h2>Preview Your Video</h2>
          <video src={videoURL} controls width="100%" />
        </div>
      )}

      <div className="step-box">
        <h2><FaUpload className="step-icon" /> Step 3: Submit Your Video</h2>
        <ul>
          <li>Click <strong>"Upload Video" OR "sub</strong>.</li>
          <li>Wait for <strong>successful upload confirmation</strong>.</li>
        </ul>
      </div>

      <div className="step-box">
        <h2><FaClipboardCheck className="step-icon" /> Step 4: Submit for Verification</h2>
        <ul>
          <li>Copy the <strong>video verification link</strong>.</li>
          <li>Share it with the <strong>support team</strong> for approval.</li>
        </ul>
      </div>

      <button 
        className="verify-button" 
        onClick={() => window.open("https://certificate.pantasign.com/Account/SignUp", "_blank")}
      >
        Start Video Verification
      </button>
    </div>
  );
};

export default VideoVerificationSteps;
