import React, { useState } from "react";
import { Link } from "react-router-dom";
import PhoneIcon from "@mui/icons-material/Phone";
import HomeIcon from "@mui/icons-material/Home";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import AccountBalanceIcon from "@mui/icons-material/AccountBalance";
import VerifiedUserIcon from "@mui/icons-material/VerifiedUser";
import "./header.css";
import logo from "../../assets/images/logo.png";

export const Header = () => {
  const [clickedLink, setClickedLink] = useState(null);

  const handleLinkClick = (linkName) => {
    setClickedLink(linkName);
    setTimeout(() => setClickedLink(null), 600); // Remove the class after animation duration
  };

  return (
    <header className="header">
      <div className="logo">
        <img src={logo} alt="logo" className="logo-image" />
      </div>
      <nav>
        <ul>
          <li>
            <Link
              to="/"
              className={clickedLink === "home" ? "clicked" : ""}
              onClick={() => handleLinkClick("home")}
            >
              <HomeIcon className="nav-icon" /> Home
            </Link>
          </li>
          <li>
            <Link
              to="/accounting-services"
              className={clickedLink === "accounting" ? "clicked" : ""}
              onClick={() => handleLinkClick("accounting")}
            >
              <AccountBalanceIcon className="nav-icon" /> Accounting Services
            </Link>
          </li>
          <li>
            <Link
              to="/dsc"
              className={clickedLink === "dsc" ? "clicked" : ""}
              onClick={() => handleLinkClick("dsc")}
            >
              <VerifiedUserIcon className="nav-icon" /> DSC Services
            </Link>
          </li>
          <li>
            <Link
              to="/about"
              className={clickedLink === "about" ? "clicked" : ""}
              onClick={() => handleLinkClick("about")}
            >
              <AccountCircleIcon className="nav-icon" /> About Us
            </Link>
          </li>
        </ul>
      </nav>
      <div className="cta-section">
        <div className="cta-button">
          <button>
            <PhoneIcon className="cta-icon" /> CONTACT US
          </button>
        </div>
      </div>
    </header>
  );
};
