import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import PhoneIcon from "@mui/icons-material/Phone";
import HomeIcon from "@mui/icons-material/Home";
import { BsWhatsapp } from "react-icons/bs";
import EmailIcon from "@mui/icons-material/Email";
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import AccountBalanceIcon from "@mui/icons-material/AccountBalance";
import VerifiedUserIcon from "@mui/icons-material/VerifiedUser";import "./header.css";
import logo from "../../assets/images/logo.png";

export const Header = () => {
  const [showIcons, setShowIcons] = useState(false);
  const ctaButtonRef = useRef(null);
  const iconsRef = useRef(null);

  const handleContactClick = () => {
    setShowIcons((prev) => !prev); 
  };

  const handleMouseLeave = (event) => {
    // Check if mouse has left the CTA button and the contact icons container
    if (
      ctaButtonRef.current &&
      iconsRef.current &&
      !ctaButtonRef.current.contains(event.relatedTarget) &&
      !iconsRef.current.contains(event.relatedTarget)
    ) {
      setShowIcons(false);
    }
  };

  useEffect(() => {
    const ctaButtonElement = ctaButtonRef.current;
    const iconsElement = iconsRef.current;

    if (ctaButtonElement && iconsElement) {
      ctaButtonElement.addEventListener("mouseleave", handleMouseLeave);
      iconsElement.addEventListener("mouseleave", handleMouseLeave);
    }

    return () => {
      if (ctaButtonElement) {
        ctaButtonElement.removeEventListener("mouseleave", handleMouseLeave);
      }
      if (iconsElement) {
        iconsElement.removeEventListener("mouseleave", handleMouseLeave);
      }
    };
  }, []);

  return (
    <header className="header">
        <div className="logo">
        <img src = {logo} alt= "logo" className="logo-image"/>
      </div>
      <nav>
        <ul>
          <li>
          <Link to="/"><HomeIcon className="nav-icon" /> Home</Link> 
          </li>
          <li>
          <Link to="/accounting-services"><AccountBalanceIcon className="nav-icon" /> Accounting Services</Link> 
          </li>
          <li>
          <Link to="/dsc"><VerifiedUserIcon className="nav-icon" /> DSC Services</Link> 
          </li>
          <li>
          <Link to="/about"><AccountCircleIcon className="nav-icon" /> About Us</Link>
          </li>
        </ul>
      </nav>
      <div className="cta-section">
        <div className="cta-button" ref={ctaButtonRef}>
          {!showIcons ? (
            <button onClick={handleContactClick}>
              <PhoneIcon className="cta-icon" /> CONTACT US
            </button>
          ) : (
            <div
              className={`contact-icons ${showIcons ? "show" : ""}`}
              ref={iconsRef}
            >
              <a
                href="https://wa.me/9490324586?text=Hello%2C%20I%20would%20like%20to%20inquire%20about%20your%20services"
                target="_blank"
                rel="noopener noreferrer"
              >
                <BsWhatsapp className="icon BsWhatsapp" />
              </a>
              {/* <a
                href="https://mail.google.com/mail/?view=cm&fs=1&to=murarisolutions.india@gmail.com&su=Inquiry%20about%20services&body=Hello%2C%20I%20would%20like%20to%20know%20more%20about%20your%20services."
                target="_blank"
                rel="noopener noreferrer"
              >
                <EmailIcon className="icon EmailIcon" />
              </a> */}
            </div>
          )}
        </div>
        {/* <div className="user-icon">
          <Link to="/user">
            <AccountCircleIcon />
          </Link>
        </div> */}
      </div>
    </header>
  );
};
